var app=angular.module("calculatorApp",[]);app.controller("CalculatorController",function($scope){$scope.result="";$scope.operator="";$scope.number1=0;$scope.number2=0;$scope.decimalAdded=false;$scope.number=function(num){if($scope.operator===""){$scope.number1=$scope.number1*10+num;$scope.result=$scope.number1}else{$scope.number2=$scope.number2*10+num;$scope.result=$scope.number2}};$scope.add=function(){$scope.operator="+";$scope.result=""};$scope.subtract=function(){$scope.operator="-";$scope.result=""};$scope.multiply=function(){$scope.operator="*";$scope.result=""};$scope.divide=function(){$scope.operator="/";$scope.result=""};$scope.decimal=function(){if($scope.decimalAdded===false){$scope.decimalAdded=true;if($scope.operator===""){$scope.number1+=".";$scope.result=$scope.number1}else{$scope.number2+=".";$scope.result=$scope.number2}}};$scope.clear=function(){$scope.result="";$scope.operator="";$scope.number1=0;$scope.number2=0;$scope.decimalAdded=false};$scope.calculate=function(){if($scope.operator==="+"){$scope.result=$scope.number1+$scope.number2}else if($scope.operator==="-"){$scope.result=$scope.number1-$scope.number2}else if($scope.operator==="*"){$scope.result=$scope.number1*$scope.number2}else if($scope.operator==="/"){$scope.result=$scope.number1/$scope.number2}$scope.operator="";$scope.number1=$scope.result;$scope.number2=0;$scope.decimalAdded=false}});